(function(w,d){"use strict";var v=d.getElementsByTagName("img");var e=function(){if(typeof(v)==="object"){for(var i=0,n=v.length;i<n;i++){if(v[i].offsetTop<(parseInt(w.innerHeight||d.documentElement.clientHeight)+parseInt(w.scrollY||d.documentElement.scrollTop))&&typeof(v[i].getAttribute("data-src"))==="string"){if(v[i].getAttribute("data-src")!=v[i].getAttribute("src")){v[i].setAttribute("src",v[i].getAttribute("data-src").length>0?v[i].getAttribute("data-src"):v[i].getAttribute("src"));}}}}};e();w.onscroll=e;}(window,document));